import jsonData from '../../json_data/merged_file.json' // Assuming the JSON file is named 'data.json' and located in the same folder

const ChartComponent = () => {
  // Extracting required data from the JSON
  console.log(jsonData)
  const timestamps = jsonData.map((item) => item.thermostatPacket.data.timestamp);
  const temperatures = jsonData.map((item) => item.thermostatPacket.data.outdoorTemperature10xF);

  // Creating the chart options and series data
  const chartOptions = {
    chart: {
      id: 'temperature-chart',
    },
    xaxis: {
      categories: timestamps,
    },
  };

  const seriesData = [
    {
      name: 'Outdoor Temperature',
      data: temperatures,
    },
  ];

  return (
    <ReactApexChart options={chartOptions} series={seriesData} type="line" height={400} />
  );
};

export default ChartComponent;
