import {Chart} from 'chart.js';

import React, { useEffect, useRef } from 'react';


const ChartComponent = () => {
  const chartRef = useRef(null);

  useEffect(() => {
    const ctx = chartRef.current.getContext('2d');

    // Initialize Chart.js chart
    const chart = new Chart(ctx, {
      type: 'bar', // Specify the chart type (e.g., bar, line, pie, etc.)
      data: {
        labels: ['Label 1', 'Label 2', 'Label 3'], // Array of labels for X-axis
        datasets: [
          {
            label: 'Dataset 1',
            data: [10, 20, 30], // Array of data values
            backgroundColor: 'rgba(0, 123, 255, 0.5)', // Background color of bars
            borderColor: 'rgba(0, 123, 255, 1)', // Border color of bars
            borderWidth: 1, // Width of bar borders
          },
        ],
      },
      options: {
        // Specify chart options and configurations
      },
    });

    // Cleanup on component unmount
    return () => {
      chart.destroy();
    };
  }, []);

  return <canvas ref={chartRef} />;
};

export default ChartComponent;
