import React, { useState, useEffect } from "react";
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import mergeData from "../../json_data/merged_file.json";
import Displaytable from "../Displaytable";

const SearchComponent = () => {
  const [searchTerm, setSearchTerm] = useState("");
  const [selectedDate, setSelectedDate] = useState(null);
  const [selectedTime, setSelectedTime] = useState(null);
  const [searchResults, setSearchResults] = useState([]);

  useEffect(() => {
    performSearch(searchTerm, selectedDate, selectedTime);
  }, [searchTerm, selectedDate, selectedTime]);

  const handleSearch = (e) => {
    const term = e.target.value;
    setSearchTerm(term);
  };

  const handleDateChange = (date) => {
    setSelectedDate(date);
  };

  const handleTimeChange = (time) => {
    setSelectedTime(time);
  };

  const performSearch = (term, date, time) => {
    const results = mergeData.filter((item) => {
      if (item.hasOwnProperty("thermostatPacket")) {
        const { product } = item.thermostatPacket.info;

        if (!date && !time) {
          // When no date or time is selected, include all items
          return product.toLowerCase().includes(term.toLowerCase());
        } else {
          // When date and/or time is selected, filter by date and/or time
          const itemDateTime = new Date(item.thermostatPacket.data.timestamp);
          const selectedDateTime = new Date(date);
          if (time) {
            itemDateTime.setHours(time.getHours());
            itemDateTime.setMinutes(time.getMinutes());
            itemDateTime.setSeconds(time.getSeconds())+'';
          }
          return (
            product.toLowerCase().includes(term.toLowerCase()) &&
            itemDateTime.getTime() === selectedDateTime.getTime()
          );
        }
      }
    });

    setSearchResults(results);
  };

  return (
    <div>
      <input
        style={{ width: "30%", height: "3vh", padding: "5px", marginBottom: "5px" }}
        type="text"
        className="search-bar"
        placeholder="Search product"
        value={searchTerm}
        onChange={handleSearch}
      />
      <DatePicker
        style={{ width: "30%", height: "3vh" }}
        selected={selectedDate}
        onChange={handleDateChange}
        placeholderText="mm/dd/yyyy"
        dateFormat="MM/dd/yyyy"
      />
      <DatePicker
        style={{ width: "30%", height: "3vh" }}
        selected={selectedTime}
        onChange={handleTimeChange}
        showTimeSelect
        showTimeSelectOnly
        timeIntervals={1}
        timeCaption="Time"
        dateFormat="HH:mm:ss"
        timeFormat="HH:mm:ss"
      />
      <table className="custom_table">
        <thead>
          <tr>
            <th>date</th>
            <th>time</th>
            <th>product</th>
            <th>version</th>
            <th>id</th>
            <th>hvacMode</th>
            <th>deadband10xF</th>
            <th>outdoorTempLockout10xF</th>
            <th>programMode</th>
            <th>fanMode</th>
            <th>numHeatStages</th>
            <th>numCompressorStages</th>
            <th>numHeatStages</th>
            <th>smartSensorCnt</th>
            <th>outdoorTemperature10xF</th>
            <th>indoorTemperature10xF</th>
            <th>outdoorHumidityPer</th>
            <th>indoorHumidityPer</th>
          </tr>
        </thead>
        <tbody>
          {searchResults.map((item, index) => (
            <Displaytable key={index} data={item} />
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default SearchComponent;
