import React, { useState, useEffect } from "react";
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import TimeSelect from "react-time-select";
import mergeData from "../../json_data/merged_file.json";
import Displaytable from "../Displaytable";

const SearchComponent = () => {
  const [searchTerm, setSearchTerm] = useState("");
  const [searchResults, setSearchResults] = useState([]);
  const [selectedDate, setSelectedDate] = useState(null);
  const [selectedTime, setSelectedTime] = useState(null);

  useEffect(() => {
    // Perform initial search on component mount
    performSearch(searchTerm, selectedDate, selectedTime);
  }, []);

  const handleSearch = (e) => {
    const searchTerm = e.target.value;
    setSearchTerm(searchTerm);
    performSearch(searchTerm, selectedDate, selectedTime);
  };

  const handleDateChange = (date) => {
    setSelectedDate(date);
    performSearch(searchTerm, date, selectedTime);
  };

  const handleTimeChange = (time) => {
    setSelectedTime(time);
    performSearch(searchTerm, selectedDate, time);
  };
  
  const performSearch = (term, date, time) => {
    const results = mergeData.filter((item) => {
      if (item.hasOwnProperty("thermostatPacket")) {
        const { product } = item.thermostatPacket.info;
        const itemTime = new Date(item.thermostatPacket.data.timestamp).toLocaleTimeString("en-US", { hour12: false });
  
        if (!date && !time) {
          // When neither date nor time is selected, include all items
          return product.toLowerCase().includes(term.toLowerCase());
        } else if (date && !time) {
          // When only date is selected, filter by date
          return (
            product.toLowerCase().includes(term.toLowerCase()) &&
            new Date(item.thermostatPacket.data.timestamp).toDateString() === date.toDateString()
          );
        } else if (!date && time) {
          // When only time is selected, filter by time
          return product.toLowerCase().includes(term.toLowerCase()) && itemTime === time;
        } else {
          // When both date and time are selected, filter by date and time
          const searchDateTime = new Date(date);
          searchDateTime.setHours(time.getHours());
          searchDateTime.setMinutes(time.getMinutes());
          searchDateTime.setSeconds(time.getSeconds());
          return (
            product.toLowerCase().includes(term.toLowerCase()) &&
            new Date(item.thermostatPacket.data.timestamp).getTime() === searchDateTime.getTime()
          );
        }
      }
    });
  
    setSearchResults(results);
  };
  
  return (
    <div>
      <input
        type="text"
        className="search-bar"
        placeholder="Search product"
        value={searchTerm}
        onChange={handleSearch}
      />
      <div className="date-time-picker">
        <DatePicker
          selected={selectedDate}
          onChange={handleDateChange}
          placeholderText="Select Date"
          dateFormat="yyyy-MM-dd"
        />
     <TimeSelect
  value={selectedTime}
  onChange={handleTimeChange}
  format="HH:mm"
  start="00:00"
  end="23:59"
  step={1}
/>
      </div>
      <table className="custom_table">
        <thead>
          <tr>
          <th>date</th>
            <th>time</th>
            <th>product</th>
             <th>version</th>
            <th>id</th>
            <th>hvacMode</th>
            <th>deadband10xF</th>
            <th>outdoorTempLockout10xF</th>
            <th>programMode</th>
           <th>fanMode</th>
           <th>numHeatStages</th>
           <th>numCompressorStages</th>
           <th>numHeatStages</th>
          <th>smartSensorCnt</th>
            <th>outdoorTemperature10xF</th>
            <th>indoorTemperature10xF</th>
           <th>outdoorHumidityPer</th>
           <th>indoorHumidityPer</th>
          </tr>
        </thead>
        <tbody>
          {searchResults.map((item, index) => (
            <Displaytable key={index} data={item} />
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default SearchComponent;
