import React, { useState, useEffect } from "react";
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import mergeData from "../../json_data/merged_file.json";
import Displaytable from "../Displaytable";

const SearchComponent = () => {
  const [searchTerm, setSearchTerm] = useState("");
  const [selectedDate, setSelectedDate] = useState(null);
  const [searchResults, setSearchResults] = useState([]);

  useEffect(() => {
    performSearch(searchTerm, selectedDate);
  }, [searchTerm, selectedDate]);

  const handleSearch = (e) => {
    const term = e.target.value;
    setSearchTerm(term);
  };

  const handleDateChange = (date) => {
    setSelectedDate(date);
  };

  // const performSearch = (term, date) => {
  //   const results = mergeData.filter((item) => {
  //     if (item.hasOwnProperty("thermostatPacket")) {
  //       const { product } = item.thermostatPacket.info;

  //       if (!date) {
  //         // When no date is selected, include all items
  //         return product.toLowerCase().includes(term.toLowerCase());
  //       } else {
  //         // When date is selected, filter by date
  //         return (
  //           product.toLowerCase().includes(term.toLowerCase()) &&
  //           new Date(item.thermostatPacket.data.timestamp).toDateString() === date.toDateString()
  //         );
  //       }
  //     }
  //   });

  //   setSearchResults(results);
  // };
  const performSearch = (term, date, time) => {
    const results = mergeData.filter((item) => {
      if (item.hasOwnProperty("thermostatPacket")) {
        const { product } = item.thermostatPacket.info;
  
        if (!date && !time) {
          // When no date and time are selected, include all items
          return product.toLowerCase().includes(term.toLowerCase());
        } else {
          // When date and/or time are selected, filter by date and time
          const itemTimestamp = new Date(item.thermostatPacket.data.timestamp);
  
          const isDateMatch = !date || itemTimestamp.toDateString() === date.toDateString();
          const isTimeMatch =
            !time ||
            itemTimestamp.getHours() === time.getHours() &&
            itemTimestamp.getMinutes() === time.getMinutes() &&
            itemTimestamp.getSeconds() === time.getSeconds();
  
          return isDateMatch && isTimeMatch && product.toLowerCase().includes(term.toLowerCase());
        }
      }
    });
  
    setSearchResults(results);
  };
  

  return (
    <div>
      <input style={{width:'30%',height:'3vh',padding:'5px',marginBottom:'5px'}}
        type="text"
        className="search-bar"
        placeholder="Search product"
        value={searchTerm}
        onChange={handleSearch}
      />
      <DatePicker style={{width:'30%',height:'3vh'}}
        selected={selectedDate}
        onChange={handleDateChange}
        placeholderText="mm/dd/yyyy"
        dateFormat="MM/dd/yyyy"
      />
     
           <table className="custom_table">
        <thead>
          <tr>
            <th>date</th>
            <th>time</th>
            <th>product</th>
            <th>version</th>
            <th>id</th>
            <th>hvacMode</th>
            <th>deadband10xF</th>
            <th>outdoorTempLockout10xF</th>
            <th>programMode</th>
            <th>fanMode</th>
            <th>numHeatStages</th>
            <th>numCompressorStages</th>
            <th>numHeatStages</th>
            <th>smartSensorCnt</th>
            <th>outdoorTemperature10xF</th>
            <th>indoorTemperature10xF</th>
            <th>outdoorHumidityPer</th>
            <th>indoorHumidityPer</th>
          </tr>
        </thead>
        {searchResults.map((item, index) => (
          <Displaytable data={item} />
        ))}
      </table>
    </div>
  );
};

export default SearchComponent;
